sys_script_include.script:
var IncidentYAMLExporter = Class.create();
IncidentYAMLExporter.prototype = {
    initialize: function() {},

    toYAML: function(incidentSysId) {
        var inc = new GlideRecord("x_1800372_job_port_github_user_input");
        if (!inc.get(incidentSysId)) {
            return "incident: {}\n";
        }

        var incidentObj = {
          //  number: inc.getValue("number"),
            short_description: inc.getValue("short_description"),
            description: inc.getValue("description"),
			user_name: inc.user_name.getDisplayValue()
            // priority: inc.getValue("priority"),
            // state: inc.getValue("state"),
            // assignment_group: inc.assignment_group.getDisplayValue(),
            // assigned_to: inc.assigned_to.getDisplayValue(),
            // opened_at: inc.getValue("opened_at"),
            // closed_at: inc.getValue("closed_at"),
            // caller: inc.caller_id.getDisplayValue()
        };

        // Convert to YAML manually
        var yaml = "incident:\n";
        for (var key in incidentObj) {
            if (incidentObj.hasOwnProperty(key)) {
                var value = incidentObj[key];
                if (value == null || value === "") {
                    value = "null";
                } else if (typeof value === "string" && value.indexOf(":") !== -1) {
                    // Quote strings with special chars like ":" to avoid YAML errors
                    value = '"' + value.replace(/"/g, '\\"') + '"';
                }
                yaml += "  " + key + ": " + value + "\n";
            }
        }
        return yaml;
    },

    type: 'IncidentYAMLExporter'
};
